$(document).ready(function(){$(".informação-nutricional").click(function(event){const infoBox=$(".InformationBox");if(infoBox.hasClass("visible")){infoBox.removeClass("visible").addClass("hidden")}else{infoBox.removeClass("hidden").addClass("visible")}event.stopPropagation()});$(document).on('click',function(event){const infoBox=$('.InformationBox');const button=$('.informação-nutricional');if(!infoBox.is(event.target)&&infoBox.has(event.target).length===0&&!button.is(event.target)){infoBox.removeClass("visible").addClass("hidden")}});function handleBurgerMenu(){if($(window).width()<=800){$('.burger-menu').show();$('#burger-toggle').off('click').on('click',function(){$('#nav-links').toggleClass('active');})}else{$('#nav-links').removeClass('active').show();$('.burger-menu').hide()}}handleBurgerMenu();if($(window).width()<=868){$(window).resize(handleBurgerMenu);$(window).scroll(function(){if($('#nav-links').hasClass('active')){$('#nav-links').css('position','fixed')}else{$('#nav-links').css('position','absolute')}})}});function adjustVisibility(){const $ingredientsContainer=$('.container-ingredients2');const $directionsContainer=$('.container-directions2');let $ingredientsButton=$('.ingredients-btn');let $instructionsButton=$('.instructions-btn');if($(window).width()<=768){if(!$ingredientsButton.length&&!$instructionsButton.length){$ingredientsButton=$('.ingredient-title').addClass('toggle-button ingredients-btn');$instructionsButton=$('.instruction-title').addClass('toggle-button instructions-btn');$('.content2').append($ingredientsButton).append($instructionsButton);$ingredientsButton.click((e)=>{e.stopPropagation();e.preventDefault();$ingredientsContainer.addClass('visible');$directionsContainer.removeClass('visible');$ingredientsButton.addClass('shadow-effect-ingredients').removeClass('shadow-effect-ingredients2');$instructionsButton.addClass('shadow-effect-instructions2').removeClass('shadow-effect-instructions')});$instructionsButton.click((e)=>{e.stopPropagation();e.preventDefault();$directionsContainer.addClass('visible');$ingredientsContainer.removeClass('visible');$instructionsButton.addClass('shadow-effect-instructions').removeClass('shadow-effect-instructions2');$ingredientsButton.addClass('shadow-effect-ingredients2').removeClass('shadow-effect-ingredients')})}if(!$ingredientsContainer.hasClass('visible')&&!$directionsContainer.hasClass('visible')){$ingredientsContainer.addClass('visible');$directionsContainer.removeClass('visible')}}else{$ingredientsContainer.addClass('visible').css('height','auto');$directionsContainer.addClass('visible').css('height','auto');$('.toggle-button').remove();}}let resizeTimeout;$(window).resize(()=>{clearTimeout(resizeTimeout);resizeTimeout=setTimeout(adjustVisibility,100)});$(document).ready(adjustVisibility);function updateHeights(){const navbar=document.getElementById('nav');const footer=document.getElementById('footer');const navbarHeight=navbar.offsetHeight+'px';const footerHeight=footer.offsetHeight;const footerStyles=window.getComputedStyle(footer);const footerMargin=parseInt(footerStyles.marginTop)+parseInt(footerStyles.marginBottom);const totalFooterHeight=footerHeight+footerMargin+'px';document.documentElement.style.setProperty('--navbar-height',navbarHeight);document.documentElement.style.setProperty('--footer-height',totalFooterHeight)}window.addEventListener('load',updateHeights);window.addEventListener('resize',updateHeights);